							{Array.from({
								length: getDaysInMonth(new Date(year, meseCorrente)),
							}).map((_, day) => (
								<div key={day + 1} className="date-cell">
									<button onClick={handleDateClick}>{day + 1}</button>
								</div>
							))}
						</div>
					</div>
				</div>
			)}
		</>
	);
}
